import React from 'react';
import {TextBoxProps as Props} from './TextBox.interface';

import {
  TextBox as TB,
  TextBoxProps as TBP,
} from '../../../../Components/TextBox';
import {FormContext, setCusotmElementValidity} from '../../FormRenderer.utils';

import cn from './TextBox.module.scss';

export function TextBox(props: Props) {
  const {submitted} = React.useContext(FormContext);

  const [value, setValue] = React.useState<string>();

  React.useEffect(() => {
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  }, [submitted]);

  React.useEffect(() => {
    setValue(props.value);
  }, [props.value]);

  const onChange: TBP['onChange'] = (e: any) => {
    setValue(e.target.value);
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  };

  return (
    <TB
      id={props.id}
      label={props.label}
      name={props.name}
      value={value}
      pattern={props.pattern}
      className={props.className}
      required={props.required}
      minLength={props.minLength}
      maxLength={props.maxLength}
      invalidInputMessage={props.invalidInputMessage}
      isSubmitted={props.isSubmitted}
      disabled={props.disabled}
      placeholder={props.placeholder}
      onChange={onChange}
    />
  );
}
