import React from 'react';
import {NumberBoxProps as Props} from './NumberBox.interface';

import {
  NumberBox as NB,
  NumberBoxProps as NBP,
} from '../../../../Components/NumberBox';
import {FormContext, setCusotmElementValidity} from '../../FormRenderer.utils';

import cn from './NumberBox.module.scss';

export function NumberBox(props: Props) {
  const {submitted} = React.useContext(FormContext);

  const [value, setValue] = React.useState<string>();

  React.useEffect(() => {
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  }, [submitted]);

  React.useEffect(() => {
    setValue(props.value);
  }, [props.value]);

  const onChange: NBP['onChange'] = (e: any) => {
    setValue(e.target.value);
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  };

  return (
    <NB
      id={props.id}
      label={props.label}
      name={props.name}
      value={value}
      className={props.className}
      required={props.required}
      min={props.min}
      max={props.max}
      invalidInputMessage={props.invalidInputMessage}
      disabled={props.disabled}
      placeholder={props.placeholder}
      onChange={onChange}
    />
  );
}
