import React from 'react';
import {SelectBoxProps as Props} from './SelectBox.interface';

import {
  SelectBox as SB,
  SelectBoxProps as SBP,
  Option,
} from '../../../../Components/SelectBox';
import {FormContext, setCusotmElementValidity} from '../../FormRenderer.utils';

import cn from './SelectBox.module.scss';

export function SelectBox(props: Props) {
  const {submitted} = React.useContext(FormContext);

  const [value, setValue] = React.useState<Option>();

  React.useEffect(() => {
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  }, [submitted]);

  React.useEffect(() => {
    if (props.value) setValue(props.value);
  }, [props.value]);

  const onChange: SBP['onChange'] = (e: {name: string; value: Option}) => {
    setValue(e.value);
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  };

  return (
    <SB
      id={props.id}
      value={value}
      name={props.name}
      label={props.label}
      options={props.options}
      required={props.required}
      disabled={props.disabled}
      className={props.className}
      placeholder={props.placeholder}
      invalidInputMessage={props.invalidInputMessage}
      onChange={onChange}
    />
  );
}
