import React from 'react';
import {PasswordBoxProps as Props} from './PasswordBox.interface';

import {
  PasswordBox as PB,
  PasswordBoxProps as PBP,
} from '../../../../Components/PasswordBox';
import {FormContext, setCusotmElementValidity} from '../../FormRenderer.utils';

import cn from './PasswordBox.module.scss';

export function PasswordBox(props: Props) {
  const {submitted} = React.useContext(FormContext);

  const [value, setValue] = React.useState<string>();

  React.useEffect(() => {
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  }, [submitted]);

  React.useEffect(() => {
    setValue(props.value);
  }, [props.value]);

  const onChange: PBP['onChange'] = (e: any) => {
    setValue(e.target.value);
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  };

  return (
    <PB
      id={props.id}
      label={props.label}
      name={props.name}
      value={value}
      onChange={onChange}
      pattern={props.pattern}
      className={props.className}
      required={props.required}
      minLength={props.minLength}
      maxLength={props.maxLength}
      invalidInputMessage={props.invalidInputMessage}
      isSubmitted={props.isSubmitted}
      disabled={props.disabled}
      placeholder={props.placeholder}
    />
  );
}
