import React from 'react';
import {Box as B} from '../../CanvasComponents';
import {
  getFlexDirection,
  getPaddingTop,
  getPaddingRight,
  getPaddingBottom,
  getPaddingLeft,
} from './Box.utils';

export function Box(props: any) {
  const style = React.useMemo(
    () => ({
      flexDirection: getFlexDirection(props.options.flexDirection),
      paddingTop: getPaddingTop(props.options.paddingTop),
      paddingRight: getPaddingRight(props.options.paddingRight),
      paddingBottom: getPaddingBottom(props.options.paddingBottom),
      paddingLeft: getPaddingLeft(props.options.paddingLeft),
    }),
    [props.options]
  );

  return <B style={style}>{props.children}</B>;
}
