import * as React from 'react';
import {Option} from '../Select.interface';
import {SelectTagProps as Props} from './SelectTag.interface';

/**
 * SelectTag
 *  Input Select Tag to associate with Component
 *  This tag is essentially the storage that holds the input and is generally used
 *  fire input events and stuff
 */
export function SelectTag(props: Props): JSX.Element {
  const testId = `${props.testId}_select`;

  const option = (opt: Option) => (
    <option key={opt.id} value={opt.value}>
      {opt.label}
    </option>
  );

  const {options = []} = props;

  const onChange = () => {};

  return (
    <select
      aria-readonly={true}
      id={props.id}
      name={props.name}
      value={props.value}
      data-testid={testId}
      onChange={onChange}
      required={props.required}
      disabled={props.disabled}
      className={props.className}>
      {options.map(option)}
    </select>
  );
}
