import React from 'react';
import {LabelProps as Props} from './Label.interface';

import cn from './Label.module.scss';

export function Label(props: Props) {
  const className = `${props.required ? cn.req : ''} ${props.className}`;

  return (
    <label htmlFor={props.for} className={className}>
      {props.text}
    </label>
  );
}
