import React from 'react';
import {TextBoxProps as Props} from './TextBox.interface';

import {Text} from '../Text';
import {Label} from '../Label';
import {Hint} from '../Hint';
import {getOnKey, KEY} from '../../Utils/commonUtils';

import cn from './TextBox.module.scss';

export function TextBox(props: Props) {
  const [valid, setValid] = React.useState(true);

  const onEnterKey = getOnKey(KEY.ENTER);

  const getClassName = (ele = '') =>
    `${cn[`tbox${ele}`]} ${props.className}${ele}`;

  const onKeyUp: Props['onKeyUp'] = (e) => {
    if (props.onKeyUp) props.onKeyUp(e);
    onEnterKey(() => {
      if (props.onEnterKey) props.onEnterKey(e);
    }, e);
  };

  const onBlur: Props['onBlur'] = (e) => {
    const {validity} = e.target as HTMLInputElement;
    setValid(validity.valid);
    if (props.onBlur) props.onBlur(e);
  };

  const getHintShow = (): boolean => {
    if (props.isSubmitted) {
      return !valid;
    } else return false;
  };

  return (
    <div className={getClassName()}>
      <Label
        for={props.id}
        text={props.label}
        required={props.required}
        className={getClassName('_label')}
      />
      <Text
        id={props.id}
        name={props.name}
        value={props.value}
        defaultValue={props.defaultValue}
        pattern={props.pattern}
        minLength={props.minLength}
        maxLength={props.maxLength}
        className={getClassName('_input')}
        disabled={props.disabled}
        required={props.required}
        onChange={props.onChange}
        onBlur={onBlur}
        onFocus={props.onFocus}
        onKeyUp={onKeyUp}
        placeholder={props.placeholder}
      />
      <Hint
        show={getHintShow()}
        message={props.invalidInputMessage}
        className={getClassName('_hint')}
      />
    </div>
  );
}
