import React from 'react';
import {FieldsetProps as Props} from './Fieldset.interface';

import cn from './Fieldset.module.scss';

export function Fieldset(props: Props) {
  const legendClass = `${cn.legend}  ${props.legendClass}`;

  const mainClass = `${cn.fset} ${props.mainClass}`;

  const onClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (props.onClick) {
      props.onClick();
    }
  };

  return (
    <fieldset className={mainClass} style={props.style} onClick={onClick}>
      <legend className={legendClass} style={props.legendStyle}>
        {props.title}
      </legend>
      {props.children}
    </fieldset>
  );
}
