import React from 'react';
import {INPUT} from '../../Utils/constants';
import {NumberProps as Props} from './Number.interface';

export function Number(props: Props) {
  const onChange: Props['onChange'] = (e) => {
    if (props.onChange) {
      props.onChange(e);
    }
  };
  const onFocus: Props['onFocus'] = (e) => {
    if (props.onFocus) {
      props.onFocus(e);
    }
  };
  const onBlur: Props['onBlur'] = (e) => {
    if (props.onBlur) {
      props.onBlur(e);
    }
  };
  const onKeyUp: Props['onKeyUp'] = (e) => {
    if (props.onKeyUp) {
      props.onKeyUp(e);
    }
  };

  return (
    <input
      type={INPUT.NUMBER}
      id={props.id}
      name={props.name}
      value={props.value}
      defaultValue={props.defaultValue}
      min={props.min}
      max={props.max}
      onChange={onChange}
      onFocus={onFocus}
      onBlur={onBlur}
      onKeyUp={onKeyUp}
      className={props.className}
      required={props.required}
      disabled={props.disabled}
      placeholder={props.placeholder}
    />
  );
}
