import React from 'react';

export enum KEY {
  ENTER = 'Enter',
}

export function getOnKey(key: KEY) {
  return (
    callback: (eve: React.KeyboardEvent) => void,
    eve: React.KeyboardEvent
  ) => {
    if (eve.key === key) {
      callback(eve);
    }
  };
}

const throttleKeys: {[key: string]: any} = {};

export function getThrottle(key: string) {
  return (callback: Function, interval = 500) => {
    if (throttleKeys.hasOwnProperty(key)) {
      clearTimeout(throttleKeys[key]);
    }
    throttleKeys[key] = setTimeout(callback, interval);
  };
}
