import React from 'react';
import {HintProps as Props} from './Hint.interface';

import cn from './Hint.module.scss';

export function Hint(props: Props) {
  const className = `${cn.h} ${props.show ? cn.hs : ''} ${props.className}`;

  return <span className={className}>{props.message}</span>;
}
