import React from 'react';
import {RadioBox as RB} from '../../CanvasComponents';
import {FormContext} from '../../FormRenderer.utils';
import {
  getLabel,
  getName,
  getDisabled,
  getRequired,
  getClassName,
  getChecked,
  getInvalidMessage,
  getID,
  getValue,
} from './RadioBox.utils';

export function RadioBox(props: any) {
  const {submitted} = React.useContext(FormContext);
  const name = getName(props.options.name);
  const value = getValue(props.options.value);
  const checked = getChecked(props.templateValues, name)(value);
  return (
    <RB
      name={name}
      value={value}
      checked={checked}
      id={getID(props.options.id)}
      label={getLabel(props.options.label)}
      className={getClassName(props.options.className)}
      required={getRequired(props.options.required)}
      invalidInputMessage={getInvalidMessage(props.options.invalidMessage)}
      disabled={getDisabled(props.options.disabled)}
      isSubmitted={submitted}
    />
  );
}
