import React from 'react';
import {FormContext} from '../../FormRenderer.utils';
import {CheckBox as CB} from '../../CanvasComponents';
import {
  getLabel,
  getName,
  getDisabled,
  getRequired,
  getClassName,
  getChecked,
  getInvalidMessage,
  getID,
  getValue,
} from './CheckBox.utils';

export function CheckBox(props: any) {
  const {submitted} = React.useContext(FormContext);
  return (
    <CB
      id={getID(props.options.id)}
      label={getLabel(props.options.label)}
      name={getName(props.options.name)}
      className={getClassName(props.options.className)}
      required={getRequired(props.options.required)}
      checked={getChecked(props.options.checked)}
      invalidInputMessage={getInvalidMessage(props.options.invalidMessage)}
      disabled={getDisabled(props.options.disabled)}
      value={getValue(props.options.value)}
      isSubmitted={submitted}
    />
  );
}
