import React from 'react';
import {ChooseComponentProps as Props} from './ChooseComponent.interface';

import {COMPONENTS} from './ChooseComponent.constants';
import {ComponentOption} from './ComponentOption';

import cn from './ChooseComponent.module.scss';

export function ChooseComponent(props: Props) {
  const renderComponent = (c: typeof COMPONENTS[0]) => (
    <ComponentOption key={c.value} pathId={c.value} title={c.title} />
  );

  return (
    <div className={cn.complist}>
      <div className={cn.complist_head}>
        <p className={cn.title}>Input Element</p>
      </div>
      <ul className={cn.complist_list}>{COMPONENTS.map(renderComponent)}</ul>
    </div>
  );
}
