import React from 'react';
import {ComponentOptionProps as Props} from './ComponentOption.interface';
import cn from './ComponentOption.module.scss';

export const DD_KEY = 'component/pathId';

export function ComponentOption(props: Props) {
  const onDragStart = (event: React.DragEvent) => {
    event.dataTransfer.setData(DD_KEY, props.pathId);
  };

  return (
    <li className={cn.option} draggable={true} onDragStart={onDragStart}>
      <span className={cn.option_icon}>â§‰</span>
      <span className={cn.option_label}>{props.title}</span>
    </li>
  );
}
