export type ColKeyValues = {
  input: {[key: string]: string | boolean | number};
  title: string;
};

export type OptItem = {
  [key: string]: string;
};

export interface TableProps {
  /**
   * Title of Option
   */
  title: string;
  /**
   * Columns for Table
   */
  columns: Record<string, ColKeyValues>;
  /**
   * list of optionItems configured
   */
  optionItems: Array<OptItem>;
}
