import React, {MouseEventHandler} from 'react';
import {ExportProps as Props} from './Export.interface';
import {Selector} from '../../Store';
import {useAppSelector} from '../../StoreProvider';

export function Export(props: Props) {
  const template = useAppSelector(Selector.getTemplate);
  const templateName = useAppSelector(Selector.getTemplateName);
  const onClick: MouseEventHandler<HTMLSpanElement> = (_) => {
    props.onExport({name: templateName, template: template});
  };
  return (
    <span className={props.className} onClick={onClick}>
      Export
    </span>
  );
}
