import React from 'react';
import {DateBox as DB} from '../../CanvasComponents';
import {
  getLabel,
  getName,
  getDisabled,
  getRequired,
  getClassName,
  getMax,
  getMin,
  getInvalidMessage,
  getID,
} from './DateBox.utils';

export function DateBox(props: any) {
  return (
    <DB
      path={props.path}
      pathId={props.pathId}
      id={getID(props.options.id)}
      label={getLabel(props.options.label)}
      name={getName(props.options.name)}
      className={getClassName(props.options.className)}
      required={getRequired(props.options.required)}
      min={getMin(props.options.min)}
      max={getMax(props.options.max)}
      invalidInputMessage={getInvalidMessage(props.options.invalidMessage)}
      disabled={getDisabled(props.options.disabled)}
    />
  );
}
