import React from 'react';
import {Title as TB} from '../../CanvasComponents';
import {
  getValue,
  getClassName,
  getID,
  getPaddingTop,
  getPaddingRight,
  getPaddingBottom,
  getPaddingLeft,
} from './Title.utils';

export function Title(props: any) {
  const style = React.useMemo(
    () => ({
      paddingTop: getPaddingTop(props.options.paddingTop),
      paddingRight: getPaddingRight(props.options.paddingRight),
      paddingBottom: getPaddingBottom(props.options.paddingBottom),
      paddingLeft: getPaddingLeft(props.options.paddingLeft),
    }),
    [props.options]
  );

  return (
    <TB
      path={props.path}
      pathId={props.pathId}
      id={getID(props.options.id)}
      Value={getValue(props.options.Value)}
      className={getClassName(props.options.className)}
      style={style}
    />
  );
}
