import {Path, PathType} from '../../../../Utils/pathUtils';

/**
 * Get index of last pathNode in component path
 * @param path Component path
 * @returns index
 */
export function getUpdatingNodeIndex(path: PathType): number {
  const parsedNodes = Path.parseToNodes(path);
  const [index] = Path.parseToIndex(parsedNodes[parsedNodes.length - 1]);
  return typeof index === 'string' ? parseInt(index) : index;
}
