import {TemplateState} from './Store.interface';
import {getSelectedElement} from './Store.utils';

export const getTemplate = (state: TemplateState) => state.template;

export const getSelectedNodePath = (state: TemplateState) =>
  state.selectedNodePath;

// export const getCurrentSideMenu = (state: TemplateState) => state.sideMenu;

export const getSelectedNode = (state: TemplateState) =>
  getSelectedElement(state);

export const getTemplateName = (state: TemplateState) => state.templateName;

export const getTemlateId = (state: TemplateState) =>
  state.templateId ? state.templateId : '--';

export const getIsPreview = (state: TemplateState) => state.isPreview;
