import React from 'react';
import {CheckBoxProps as Props} from './CheckBox.interface';

import {
  CheckBox as CB,
  CheckBoxProps as CBP,
} from '../../../../Components/CheckBox';
import {FormContext, setCusotmElementValidity} from '../../FormRenderer.utils';

import cn from './CheckBox.module.scss';

export function CheckBox(props: Props) {
  const {submitted} = React.useContext(FormContext);
  const [checked, setChecked] = React.useState<boolean>(props.checked);
  React.useEffect(() => {
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  }, [submitted]);
  React.useEffect(() => {
    setChecked(props.checked);
  }, [props.checked]);

  const onChange: CBP['onChange'] = (e: any) => {
    setChecked(e.target.checked);
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  };

  return (
    <CB
      id={props.id}
      label={props.label}
      name={props.name}
      className={props.className}
      required={props.required}
      value={props.value}
      invalidInputMessage={props.invalidInputMessage}
      isSubmitted={props.isSubmitted}
      disabled={props.disabled}
      checked={checked}
      onChange={onChange}
    />
  );
}
