import React from 'react';
import {DateBoxProps as Props} from './DateBox.interface';

import {
  DateBox as DB,
  DateBoxProps as DBP,
} from '../../../../Components/DateBox';
import {FormContext, setCusotmElementValidity} from '../../FormRenderer.utils';

import cn from './DateBox.module.scss';

export function DateBox(props: Props) {
  const {submitted} = React.useContext(FormContext);

  const [value, setValue] = React.useState<string>();

  React.useEffect(() => {
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  }, [submitted]);

  React.useEffect(() => {
    setValue(props.value);
  }, [props.value]);

  const onChange: DBP['onChange'] = (e: any) => {
    setValue(e.target.value);
    if (submitted) {
      setCusotmElementValidity(props.id, props.invalidInputMessage);
    }
  };

  return (
    <DB
      id={props.id}
      label={props.label}
      name={props.name}
      value={value}
      className={props.className}
      required={props.required}
      min={props.min}
      max={props.max}
      invalidInputMessage={props.invalidInputMessage}
      disabled={props.disabled}
      onChange={onChange}
    />
  );
}
