import {ComponentOptionItemType as OptionType} from '../../../Comp.interface';

export function getFlexDirection(option: OptionType) {
  return option.input.value;
}

export function getPaddingTop(option: OptionType) {
  return `${option.input.value}${option.attribute.unit}`;
}

export function getPaddingRight(option: OptionType) {
  return `${option.input.value}${option.attribute.unit}`;
}

export function getPaddingBottom(option: OptionType) {
  return `${option.input.value}${option.attribute.unit}`;
}

export function getPaddingLeft(option: OptionType) {
  return `${option.input.value}${option.attribute.unit}`;
}
