import React from 'react';
import {PasswordBox as PB} from '../../CanvasComponents';
import {
  getLabel,
  getName,
  getDisabled,
  getRequired,
  getPlaceholder,
  getClassName,
  getPattern,
  getMaxLength,
  getMinLength,
  getInvalidMessage,
  getID,
  getValue,
} from './PasswordBox.utils';

export function PasswordBox(props: any) {
  const name = getName(props.options.name);
  const value = getValue(props.templateValues, name);
  return (
    <PB
      name={name}
      value={value}
      id={getID(props.options.id)}
      label={getLabel(props.options.label)}
      pattern={getPattern(props.options.pattern)}
      className={getClassName(props.options.className)}
      required={getRequired(props.options.required)}
      minLength={getMinLength(props.options.minLength)}
      maxLength={getMaxLength(props.options.maxLength)}
      invalidInputMessage={getInvalidMessage(props.options.invalidMessage)}
      disabled={getDisabled(props.options.disabled)}
      placeholder={getPlaceholder(props.options.placeholder)}
    />
  );
}
