import React from 'react';
import {SelectBox as SB} from '../../CanvasComponents';
import {
  getLabel,
  getName,
  getDisabled,
  getRequired,
  getPlaceholder,
  getClassName,
  getInvalidMessage,
  getID,
  getOptions,
  getValue,
} from './SelectBox.utils';

export function SelectBox(props: any) {
  const name = getName(props.options.name);
  const value = getValue(props.templateValues, props.options);

  return (
    <SB
      name={name}
      value={value}
      id={getID(props.options.id)}
      label={getLabel(props.options.label)}
      options={getOptions(props.options.options)}
      required={getRequired(props.options.required)}
      disabled={getDisabled(props.options.disabled)}
      placeholder={getPlaceholder(props.options.placeholder)}
      className={getClassName(props.options.className)}
      invalidInputMessage={getInvalidMessage(props.options.invalidMessage)}
    />
  );
}
