import React from 'react';
import {BoxProps as Props} from './Box.interface';

import cn from './Box.module.scss';

export function Box(props: Props) {
  const className = `${cn.box} ${props.className}`;

  const onClick = (e: any) => {
    e.stopPropagation();
    if (props.onClick) {
      props.onClick();
    }
  };

  return (
    <div className={className} style={props.style} onClick={onClick}>
      {props.children}
    </div>
  );
}
