import React from 'react';
import {CheckProps as Props} from './Check.interface';
import {INPUT} from '../../Utils/constants';

export function Check(props: Props) {
  const onChange: Props['onChange'] = (e) => {
    if (props.onChange) {
      props.onChange(e);
    }
  };

  return (
    <input
      id={props.id}
      name={props.name}
      value={props.value}
      checked={props.checked}
      onChange={onChange}
      className={props.className}
      required={props.required}
      type={INPUT.CHECKBOX}
      disabled={props.disabled}
    />
  );
}
