import React from 'react';
import {INPUT} from '../../Utils/constants';
import {EmailProps as Props} from './Email.interface';

export function Email(props: Props) {
  const onChange: Props['onChange'] = (e) => {
    if (props.onChange) {
      props.onChange(e);
    }
  };
  const onFocus: Props['onFocus'] = (e) => {
    if (props.onFocus) {
      props.onFocus(e);
    }
  };
  const onBlur: Props['onBlur'] = (e) => {
    if (props.onBlur) {
      props.onBlur(e);
    }
  };
  const onKeyUp: Props['onKeyUp'] = (e) => {
    if (props.onKeyUp) {
      props.onKeyUp(e);
    }
  };

  return (
    <input
      type={INPUT.EMAIL}
      id={props.id}
      name={props.name}
      value={props.value}
      defaultValue={props.defaultValue}
      pattern={props.pattern}
      minLength={props.minLength}
      maxLength={props.maxLength}
      onChange={onChange}
      onFocus={onFocus}
      onBlur={onBlur}
      onKeyUp={onKeyUp}
      className={props.className}
      required={props.required}
      disabled={props.disabled}
      placeholder={props.placeholder}
    />
  );
}
