import {LabelProps} from '../Label';
import {HintProps} from '../Hint';
import {EmailProps} from '../Email';

export interface EmailBoxProps
  extends EmailProps,
    Omit<LabelProps, 'for' | 'text' | 'className' | 'required'>,
    Omit<HintProps, 'className' | 'show' | 'message'> {
  /**
   * Label for input
   */
  label: string;
  /**
   * message for invalid inputs
   */
  invalidInputMessage: string;
  /**
   * boolean to set if form submitted
   */
  isSubmitted?: boolean;
}
