export interface FieldsetProps {
  /**
   * list of child elements to be displayed in fieldset
   */
  children: JSX.Element | JSX.Element[];
  /**
   * className to set of fieldset Tag
   */
  mainClass: string;
  /**
   * className to set on legend Tag
   */
  legendClass: string;
  /**
   * Text to display on legend
   */
  title: string;
  /**
   * Style object to set on tag
   */
  style: {[key: string]: string};
  /**
   * Style object to set on legend
   */
  legendStyle: {[key: string]: string};
  /**
   * Hanlder for when clicked with mouse
   */
  onClick?: () => void;
}
