import React from 'react';

export interface RadioProps {
  /**
   * name  of input
   */
  name: string;
  /**
   * value of input
   */
  value: string;
  /**
   * Id for input
   */
  id: string;

  /**
   * boolean set if radio is checked
   */
  checked: boolean;

  /**
   * ClassName for input
   */
  className: string;
  /**
   * required for input
   */
  required: boolean;
  /**
   * disabled for input
   */
  disabled: boolean;

  /**
   * onChange event Handler
   */
  onChange?: React.ChangeEventHandler;

  /**
   * to Handle when clicked on Enter key
   */
  onEnterKey?: React.KeyboardEventHandler;
}
