import * as React from 'react';
import cn from './Focused.module.scss';

export interface FocusedProps {
  /**
   * className to set on icon
   */
  className: string;
  /**
   * Id for testing
   */
  testId?: string;
}

export function Focused(props: FocusedProps): JSX.Element {
  const className = `${cn.icon} ${props.className}`;
  const testId = `${props.testId}_icon_focused`;
  return <span data-testid={testId} className={className} />;
}
