import * as React from 'react';
import {IconProps as Props} from './Icon.interface';

import {Focused} from './Focused';
import {Normal} from './Normal';
import {Loading} from './Loading';

export function Icon(props: Props): JSX.Element {
  const renderContent = (): JSX.Element => {
    if (props.loading) {
      return <Loading testId={props.testId} className={props.iconClassName} />;
    }
    if (props.focus) {
      return <Focused testId={props.testId} className={props.iconClassName} />;
    } else {
      return <Normal testId={props.testId} className={props.iconClassName} />;
    }
  };

  return <div className={props.className}>{renderContent()}</div>;
}
