import * as React from 'react';
import cn from './Loading.module.scss';

export interface LoadingProps {
  /**
   * className to set on Element
   */
  className: string;
  /**
   * Id for testing
   */
  testId?: string;
}

export function Loading(props: LoadingProps): JSX.Element {
  const className = `${cn.icon} ${props.className}`;
  const testId = `${props.testId}_icon_loading`;
  return (
    <div data-testid={testId} className={className}>
      <span className={`${cn.box} ${cn.b1}`}></span>
      <span className={`${cn.box} ${cn.b2}`}></span>
      <span className={`${cn.box} ${cn.b3}`}></span>
    </div>
  );
}
