import * as React from 'react';
import cn from './Normal.module.scss';

export interface NormalProps {
  /**
   * className to set on icon
   */
  className: string;
  /**
   * Id for testing
   */
  testId?: string;
}

export function Normal(props: NormalProps): JSX.Element {
  const className = `${cn.icon} ${props.className}`;
  const testId = `${props.testId}_icon_normal`;
  return <span data-testid={testId} className={className} />;
}
