import * as React from 'react';
import {InputAltProps as Props} from './InputAlt.interface';

/**
 * InputAlt
 *  Component substitues Input Element In InputBlock; When unfocused
 */
export function InputAlt(props: Props): JSX.Element {
  const text = props.label ?? props.placeholder;
  return (
    <span className={props.className} data-testid={props.testId}>
      {text}
    </span>
  );
}
