export interface InputBlockProps {
  /**
   * className to set on Component
   */
  className: string;
  /**
   * value to display
   */
  value?: string;
  /**
   * placeholder to display if no value
   */
  placeholder: string;
  /**
   * Id for testing
   */
  testId: string;
  /**
   * boolean to set input disabled
   */
  disabled: boolean;
  /**
   * Invoked to call search function
   */
  onSearch: (inputText: string) => void;
  /**
   * boolean to set if input is focused
   */
  focused: boolean;
}
