import * as React from 'react';
import {InputBlockProps as Props} from './InputBlock.interface';
import {Input} from './Input';
import {InputAlt} from './InputAlt';

/**
 * InputBlock
 *  Component to handle rendering of Selected Option and Typing in Component
 */
export function InputBlock(props: Props): JSX.Element {
  if (props.focused) {
    return (
      <Input
        value={props.value}
        onSearch={props.onSearch}
        className={props.className}
        testId={`${props.testId}_input`}
      />
    );
  } else {
    return (
      <InputAlt
        label={props.value}
        className={props.className}
        placeholder={props.placeholder}
        testId={`${props.testId}_inputalt`}
      />
    );
  }
}
