import * as React from 'react';
import cn from './Loading.module.scss';

export interface LoadingProps {
  /**
   * Id for testing
   */
  testId?: string;
}

export function Loading(props: LoadingProps): JSX.Element {
  const testId = `${props.testId}_option_loading`;
  return (
    <li data-testid={testId} className={cn.loading}>
      Loading...
    </li>
  );
}
