import {Option} from '../Select.interface';

export interface OptionsProps {
  /**
   * ClassName to set on OptionItem
   */
  optionClass: string;
  /**
   * List of Options
   */
  options: Array<Option>;
  /**
   * Is options list loading
   */
  loading: boolean;
  /**
   * Hadler for when an option is selected
   */
  onSelect: (arg0: Option) => void;
  /**
   * Id for testing
   */
  testId?: string;
  /**
   * boolean to show/hide Options
   */
  show: boolean;
  /**
   * ClassName to set on Options wrapper
   */
  className: string;
}
