const throttleKeys: {[key: string]: any} = {};

export function getThrottle(key: string) {
  return (callback: () => void, interval = 500) => {
    if (throttleKeys.hasOwnProperty(key) && throttleKeys[key]) {
      clearTimeout(throttleKeys[key]);
    }

    throttleKeys[key] = setTimeout(callback, interval);
  };
}

export function getCheckIfChild(element: HTMLElement) {
  return (child: HTMLElement | null) => {
    if (child === null) return false;
    else return element.contains(child);
  };
}
