import {Option} from '../Select.interface';

export interface SelectTagProps {
  /**
   * Id for input
   */
  id?: string;
  /**
   * Id for testing
   */
  testId?: string;
  /**
   * className to set on Component
   */
  className?: string;
  /**
   * value to set on tag
   */
  value?: string;
  /**
   * name of select input
   */
  name: string;
  /**
   * boolean to set if value required
   */
  required: boolean;
  /**
   * boolean to set if disabled
   */
  disabled: boolean;
  /**
   * list of options associated with Select
   */
  options: Option[];
}
