import {Option, SelectProps} from '../Select';

export interface SelectInputProps {
  /**
   * id to set on input for accessibility
   */
  id: string;
  /**
   * name for AsyncSelect input
   */
  name: string;
  /**
   * className to set on Component
   */
  className?: string;
  /**
   * boolean to set button disabled
   */
  disabled?: boolean;
  /**
   * Selected Item
   */
  value?: Option;
  /**
   * placeholder
   */
  placeholder?: string;
  /**
   * Callback for when onChange is fired on Select
   */
  onChange: SelectProps['onSelect'];
  /**
   * id for testing
   * this will be used as a prefix and attached to each unit part
   */
  testId?: string;
  /**
   * Options of select
   */
  options: Array<Option>;
  /**
   * boolean indicating value is required
   */
  required: boolean;
}

export type {Option};
