import * as React from 'react';
import {InputProps as Props} from './Input.interface';

const TYPE = 'checkbox';

export function Input(props: Props) {
  return (
    <input
      id={props.id}
      type={TYPE}
      name={props.name}
      value={props.value}
      checked={props.checked}
      onChange={props.onChange}
      className={props.className}
    />
  );
}
